-- Optimierte HuT Tarife - Nur Zone 1, 2 und 3
-- Basierend auf den tatsächlich benötigten Kundenadressen

-- Lösche alle alten HuT Tarife
DELETE FROM partner_base_rates 
WHERE partner_id = (SELECT id FROM partners WHERE name = 'HuT');

-- ZONE01 Tarife (Stuttgart Stadt)

-- ZONE02 Tarife (u.a. Benningen)

-- ZONE03 Tarife (u.a. Affalterbach, Marbach, Steinheim)

-- Prüfe das Ergebnis
SELECT 
    zone_code,
    COUNT(*) as anzahl_tarife,
    MIN(weight_from) as min_weight,
    MAX(weight_to) as max_weight,
    MIN(base_price) as min_price,
    MAX(base_price) as max_price
FROM partner_base_rates 
WHERE partner_id = (SELECT id FROM partners WHERE name = 'HuT')
GROUP BY zone_code
ORDER BY zone_code;